import "./styles.css";
import { useEffect, useState } from "react";

export default function App() {
  const [rows, setRows] = useState([]);
  const [result, setResult] = useState(0);
  useEffect(() => {
    let temp_result = 0;
    for(const row of rows) {
      if(!row.disabled) temp_result += row.positive ? Number(row.value) : -Number(row.value);
    }
    setResult(temp_result)
  }, [rows]);

  function renderRows(){
    return (
      <div>
        {rows.map((row, index) => (
          <div key={index}>
            <select onChange={(event)=>{
              const temp_rows = [...rows];
              temp_rows[index].positive = event.target.value === "+";
              setRows(temp_rows);
            }}>
              <option selected>+</option>
              <option>-</option>
            </select>
            <input
              type="text"
              value={row.value}
              onChange={(e) => {
                const newRows = [...rows];
                newRows[index] = {
                  value : e.target.value,
                  disabled: newRows[index].disabled,
                  positive: newRows[index].positive
                };
                setRows(newRows);
              }}
            />
            <button onClick={()=>{

            }}>Delete</button>
            <button onClick={()=>{
              const newRows = [...rows];
              newRows[index] = {
                value : newRows[index].value,
                disabled: !newRows[index].disabled,
                positive: newRows[index].positive                
              };
              setRows(newRows);
            }}>{rows[index].disabled ? "Enable" : "Disable"}</button>
          </div>
        ))}
      </div>
    )
  }
  return (
    <div className="App">
      <h1>React Calculator</h1>
      <button style={{
        color: "black"
      }} onClick={()=>{
        setRows([...rows, {
          positive: false,
          value: 0,
          disabled: false
        }])
      }}>Add Row</button>
      {renderRows()}
      <h4>Result : {result}</h4>
    </div>
  );
}
